import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit{
  title = 'banner';
  currentTime: Date;

  constructor() {
    this.currentTime = new Date();
  }

  ngOnInit(): void {
    setInterval(() => {
      this.currentTime = new Date();
    }, 1000);
  }
}
<div class="container">

  <img src="../assets/Bg.jpg" alt="bg" />

  <div class="body">
    <div class="calender">
      <img src="../assets/calender.png" alt="calender"/>
    </div>
    <div class="timer">
      <div class="hour">
        <div class="num">
          <span>{{ currentTime | date: 'HH' }}</span>
        </div>
        <p class="subtitle">ساعت</p>
      </div>
      <span>:</span>
      <div class="min">
        <div class="num">
          <span>{{ currentTime | date: 'mm' }}</span> 
        </div>
        <p class="subtitle">دقیقه</p>
      </div>
      <span>:</span>
      <div class="sec">
        <div class="num">
          <span>{{ currentTime | date: 'ss' }}</span>
        </div>
        <p class="subtitle">ثانیه</p>
      </div>
    </div>


  </div>
  <div class="header">
    <div class="price-holder">
      <p>1/00/000</p>
    </div>
    <h2>قیمت بتن (تومان)</h2>
  </div> 

</div>
